import './App.css';
import Header from './components/Header';
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom';

import Characters from './pages/Characters';

import Characters_comics from './pages/Characters_comics';
import Characters_stories from './pages/Characters_stories';

import Comics from './pages/Comics';
import Stories from './pages/Stories';

const routes = [
    {
        path: "/comics",
        component: Comics
    },
    {
        path: "/stories",
        component: Stories
    },
    {
        path: "/characters",
        component: Characters,
        routes: [
        {
            path: "/comics/:id",
            component: Characters_comics
        },
        {
            path: "/stories/:id",
            component: Characters_stories
        }
        ]
    }
];
function RouteWithSubRoutes(route) {
    return (
        <Route
            path={route.path}
            render={props => (
            // pass the sub-routes down to keep nesting
            <route.component {...props} routes={route.routes} />
            )}
        />
    );
}
function App() {
  return (
    <div className="App">
        <BrowserRouter>
            <Header />
            <Switch>
                <Route exact path="/">
                    <Redirect to="/characters" />
                </Route>
                {routes.map((route, i) => (
                    <RouteWithSubRoutes key={i} {...route} />
                ))}
            </Switch>
        </BrowserRouter>
    </div>
  );
}
export default App;
